mapper.insert:

    SELECT NEXTVAL('SEQ_DELIVERY_ID') FROM dual


INSERT INTO deliver (delivery_id, member_address, delivery_status, delivery_memo, recipient_name)
SELECT #{delivery_id}, member_address, #{delivery_status}, #{delivery_memo}, #{recipient_name}
FROM member WHERE member_id = #{member_id}

mapper.update:
UPDATE delivery
SET delivery_status = #{delivery_status},
delivery_address = #{delivery_address}
WHERE delivery_id = #{delivery_id}
